<div class="flex flex-col gap-4">
    <input x-on:keyup="set_names" placeholder="label" x-model="content.label" type="text" class="input input-bordered w-full max-w-xs" >
    <input x-on:keyup="set_names" placeholder="min value" x-model="content.min" type="text" class="input input-bordered w-full max-w-xs" >
    <input x-on:keyup="set_names" placeholder="max value" x-model="content.max" type="text" class="input input-bordered w-full max-w-xs" >
    <input x-on:keyup="set_names" placeholder="step value" x-model="content.step" type="text" class="input input-bordered w-full max-w-xs" >
    <input x-on:keyup="set_names" placeholder="default value" x-model="content.default" type="text" class="input input-bordered w-full max-w-xs" >
    <input x-on:keyup="set_names" placeholder="minimum label" x-model="content.label_min" type="text" class="input input-bordered w-full max-w-xs" >
    <input x-on:keyup="set_names" placeholder="middle label" x-model="content.label_mid" type="text" class="input input-bordered w-full max-w-xs" >
    <input x-on:keyup="set_names" placeholder="maximum label" x-model="content.label_max" type="text" class="input input-bordered w-full max-w-xs" >
</div>
